package top.gn.ssm.bean;import org.springframework.stereotype.Component;import javax.validation.constraints.Pattern;@Componentpublic class Employment {    private Integer empId;    @Pattern(regexp = "^([a-z0-9_-]){4,12}|([\\u2E80-\\u9FFF]){2,4}$",            message = "员工名称4-12个数字和字母或者2-4个汉字")    private String empName;    private String gender;    @Pattern(regexp = "^[a-z\\d]+(\\.[a-z\\d]+)*@([\\da-z](-[\\da-z])?)+(\\.{1,2}[a-z]+)+$",                        message = "邮箱格式不合法")    private String emall;    private Integer deptId;    private Department department;    public Employment(){}    public Employment(Integer empId, String empName, String gender, String emall,                      Integer deptId, Department department) {        this.empId = empId;        this.empName = empName;        this.gender = gender;        this.emall = emall;        this.deptId = deptId;        this.department = department;    }    public Department getDepartment() {        return department;    }    public void setDepartment(Department department) {        this.department = department;    }    public Integer getEmpId() {        return empId;    }    public void setEmpId(Integer empId) {        this.empId = empId;    }    public String getEmpName() {        return empName;    }    public void setEmpName(String empName) {        this.empName = empName == null ? null : empName.trim();    }    public String getGender() {        return gender;    }    public void setGender(String gender) {        this.gender = gender == null ? null : gender.trim();    }    public String getEmall() {        return emall;    }    public void setEmall(String emall) {        this.emall = emall == null ? null : emall.trim();    }    public Integer getDeptId() {        return deptId;    }    public void setDeptId(Integer deptId) {        this.deptId = deptId;    }}